import React from 'react';
import { Box, Container, Typography, Grid, Avatar, Divider } from '@mui/material';
import './about.css';

const boardMembers = [
  {
    name: 'Moses Kayuni',
    title: 'Founder & Director',
    introduction: 'Moses Kayuni is the visionary behind Grace of Good Food Ministries. With a passion for serving vulnerable communities, he has dedicated his life to creating sustainable programs that provide nourishment and hope to those in need.',
    image: '/images/five.jpeg', // Placeholder image path
  },
  {
    name: 'Carol Collett',
    title: 'Executive Director',
    introduction: 'Carol Collett brings decades of experience in nonprofit management to the board. Her strategic vision and leadership have been instrumental in expanding the reach of Grace of Good Food Ministries.',
    image: '/images/four.jpeg', // Placeholder image path
  },
  {
    name: 'Kirk Belmont',
    title: 'Treasurer',
    introduction: 'Kirk Belmont is a seasoned financial expert with a heart for philanthropy. As the Treasurer of the board, Kirk oversees the financial health of Grace of Good Food Ministries.',
    image: '/images/three.jpeg', // Placeholder image path
  },
  {
    name: 'Jacenta Brandon',
    title: 'Community Outreach Coordinator',
    introduction: 'Jacenta Brandon is a community organizer with a deep understanding of the challenges faced by rural communities. Her work with local leaders has been crucial in building trust and ensuring the success of the ministry’s outreach programs.',
    image: '/images/two.jpeg', // Placeholder image path
  },
  {
    name: 'Maro Chirwa',
    title: 'Health Program Manager',
    introduction: 'Maro Chirwa is a healthcare professional with a focus on public health. As a board member, Maro advocates for the health and well-being of the children and families served by Grace of Good Food Ministries.',
    image: '/images/one.jpeg', // Placeholder image path
  },
];

const About = () => {
  return (
    <div className="about-wrapper">
      {/* Introductory Section */}
      <Box className="about-intro">
        <Container>
          <Typography variant="h2" component="h2" className="intro-title">
            Our Dynamic and Diverse Team
          </Typography>
          <Typography variant="body1" className="intro-description">
            Grace of Good Food Ministries is powered by a dedicated group of volunteers and paid staff who share a common goal: to create lasting change in our communities. We work with an incredible team of passionate individuals, including local community members and international volunteers, all committed to our mission. Together, we strive to develop skills, foster leadership, and inspire others to make a difference.
          </Typography>
        </Container>
      </Box>

      {/* Team Section */}
      <Container>
        <Grid container spacing={4} className="team-grid">
          {boardMembers.map((member, index) => (
            <Grid item xs={12} md={6} lg={4} key={index} className="team-member">
              <Box className="team-card">
                <Avatar src={member.image} alt={member.name} className="team-avatar" />
                <Box className="team-content">
                  <Typography variant="h5" component="h3" className="team-name">
                    {member.name}
                  </Typography>
                  <Typography variant="subtitle1" className="team-title">
                    {member.title}
                  </Typography>
                  <Divider className="team-divider" />
                  <Typography variant="body2" className="team-introduction">
                    {member.introduction}
                  </Typography>
                </Box>
              </Box>
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );
};

export default About;
