import React from 'react';
import { Box, Container, Typography } from '@mui/material';
import './about.css';

const boardMembers = [
  {
    name: 'Moses Kayuni',
    introduction: 'Moses Kayuni is the visionary behind Grace of Good Food Ministries. With a passion for serving vulnerable communities, he has dedicated his life to creating sustainable programs that provide nourishment and hope to those in need. Moses has a background in computer science and data analytics, which he leverages to enhance the organization’s operations and impact.',
    image: '/images/five.jpeg', // Placeholder image path
  },
  {
    name: 'Carol Collett',
    introduction: 'Carol Collett brings decades of experience in nonprofit management to the board. Her strategic vision and leadership have been instrumental in expanding the reach of Grace of Good Food Ministries. Carol is committed to fostering a culture of transparency and accountability within the organization, ensuring that every resource is utilized effectively.',
    image: '/images/four.jpeg', // Placeholder image path
  },
  {
    name: 'Kirk Belmont',
    introduction: 'Kirk Belmont is a seasoned financial expert with a heart for philanthropy. As the Treasurer of the board, Kirk oversees the financial health of Grace of Good Food Ministries, ensuring that the organization is financially sustainable and capable of growing its programs to serve more children in need.',
    image: '/images/three.jpeg', // Placeholder image path
  },
  {
    name: 'Jacenta Brandon',
    introduction: 'Jacenta Brandon is a community organizer with a deep understanding of the challenges faced by rural communities. Her work with local leaders has been crucial in building trust and ensuring the success of the ministry’s outreach programs. Jacenta’s passion for education drives her efforts to create opportunities for children to learn and thrive.',
    image: '/images/two.jpeg', // Placeholder image path
  },
  {
    name: 'Maro Chirwa',
    introduction: 'Maro Chirwa is a healthcare professional with a focus on public health. As a board member, Maro advocates for the health and well-being of the children and families served by Grace of Good Food Ministries. His expertise has been vital in developing and implementing health programs that address the specific needs of the communities the ministry serves.',
    image: '/images/one.jpeg', // Placeholder image path
  },
];

const About = () => {
  return (
    <div className="about-wrapper">
      {/* Header Image Section */}
      <Box className="about-hero-container">
        <Box className="about-intro-container">
          <Typography variant="h1" component="h1">
            Meet the Board of Trustees
          </Typography>
        </Box>
      </Box>

      {/* Board Members Section */}
      <Container>
        {boardMembers.map((member, index) => (
          <Box key={index} className="about-section" my={4}>
            <img
              src={member.image}
              alt={member.name}
              className="about-image"
            />
            <Box className="about-content">
              <Typography
                variant="h4"
                component="h3"
                gutterBottom
                className="about-title"
              >
                {member.name}
              </Typography>
              <Typography
                variant="body1"
                className="about-description"
              >
                {member.introduction}
              </Typography>
            </Box>
            {index < boardMembers.length - 1 && <hr />} {/* Add a divider between members except the last one */}
          </Box>
        ))}
      </Container>
    </div>
  );
};

export default About;
